package com.google.tendengce.actuator;import org.springframework.boot.actuate.health.Health;import org.springframework.boot.actuate.health.HealthIndicator;import org.springframework.stereotype.Component;/** * * 自定义检查  在访问/health * */@Componentpublic class SystemCheck implements HealthIndicator {    @Override    public Health health() {        int checkCode = check();        if (checkCode != 1)            return Health.down().withDetail("SystemError","the System has some unKown error!").build();        return Health.up().build();    }    private int check() {        return 1;    }}