package com.google.tendengce.controller;import org.apache.ibatis.annotations.Param;import org.springframework.cloud.netflix.eureka.EnableEurekaClient;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;@RestController@EnableEurekaClientpublic class EurekaService {    @RequestMapping("/service")    public String userService(@RequestParam(value = "name") String name){        return "i am " + name;    }}