package com.google.tendengce.druidConfig;import org.springframework.boot.web.servlet.FilterRegistrationBean;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;//@Configurationpublic class HighFilter {//    @Bean    public FilterRegistrationBean testFilterRegistration() {        FilterRegistrationBean registration = new FilterRegistrationBean();        registration.setFilter(new MyFilter());        //过滤掉/break 的请求        registration.addUrlPatterns("/break");        System.out.println("=================");        //过滤掉 /getUser 和/hello 的请求        registration.addUrlPatterns("/getUser","/hello");        //过滤掉所有请求        registration.addUrlPatterns("/*");        registration.setName("MyFilter");        registration.setOrder(1);        return registration;    }}