CREATE TABLE SUSER (   ID INT(8) NOT NULL AUTO_INCREMENT,   NAME VARCHAR(30) NOT NULL,   AGE INT(3) DEFAULT NULL,   SCORE_ID INT(8),   INFO_ID INT(8),   PRIMARY KEY(ID))ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;CREATE TABLE SCORE (   ID INT(8) NOT NULL AUTO_INCREMENT,   AMOUNT INT(10) DEFAULT 0,   VIP_FLAG VARCHAR(2) DEFAULT 'N',   CREATE_DATE DATE,   PRIMARY KEY(ID))ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;CREATE TABLE INFO (   ID INT(8) NOT NULL AUTO_INCREMENT,   INFOMATION INT(10) DEFAULT 0,   VALID_FLAG VARCHAR(2) DEFAULT 'N',   CREATE_DATE DATE,   PRIMARY KEY(ID))ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;